mapper.select:
select * from ( SELECT d.id as id, d.project_id as projectid, ifnull(d.demand_name, ' ') as demandname, d.start_time as starttime, d.end_time as endtime, d.expected_st_time as expectedsttime, d.expected_en_time as expectedentime, d.demand_content as demandcontent, d.principal as principal, ifnull(u.uname, ' ') as username, d.addtime as addtime, d.updtime as updtime, ifnull(p.project_name, ' ') as projectname, d.operator as operator, ifnull(u1.uname, ' ') as operatorname, CASE WHEN (d.start_time IS NULL or d.start_time='') AND (d.end_time IS NULL or d.end_time = '') THEN '未开始' WHEN d.start_time IS NOT NULL AND d.start_time!='' AND (d.end_time IS NULL or d.end_time = '') THEN '进行中' WHEN d.start_time IS NOT NULL AND d.start_time!='' AND d.end_time IS NOT NULL and d.end_time != '' THEN '已结束' ELSE '--' END status FROM zs_demand d left join turnin_user u on d.principal=u.uid left join zs_project p on d.project_id=p.project_id left join turnin_user u1 on d.operator=u1.uid WHERE 1 = 1
AND d.demand_name LIKE "%"#{demandname}"%"
AND d.demand_name = #{demandnamejq}
ORDER BY d.project_id DESC
LIMIT #{startindex},#{pagesize}
) s where 1=1
AND s.status = #{status}
AND s.projectname LIKE "%"#{projectname}"%"

mapper.update:
UPDATE zs_demand SET
demand_name=#{demandname}

    ,project_id=#{projectid}


    ,start_time=#{starttime}


    ,end_time=#{endtime}


    ,expected_st_time=#{expectedsttime}


    ,expected_en_time=#{expectedentime}


    ,demand_content=#{demandcontent}


    ,principal=#{principal}


    ,updTime=#{updateTime}


    ,operator=#{operator}

WHERE id= #{id};
