mapper.select:
select * from ( SELECT p.id as id, p.project_id as projectid, p.project_name as projectname, p.expected_st_time as expectedsttime, p.expected_en_time as expectedentime, p.actual_st_time as actualsttime, p.actual_en_time as actualentime, p.introduction as introduction, p.principal as principal, p.addtime as addtime, ifnull(u.uname, ' ') as username,
SUBSTRING(p.project_id,-3) as projectnumber,
p.updtime as updtimep, p.operator as operator, ifnull(u1.uname, ' ') as operatorname, CASE WHEN (p.actual_st_time IS NULL or p.actual_st_time='') AND (p.actual_en_time IS NULL or p.actual_en_time = '') THEN '未开始' WHEN p.actual_st_time IS NOT NULL AND p.actual_st_time!='' AND (p.actual_en_time IS NULL or p.actual_en_time = '') THEN '进行中' WHEN p.actual_st_time IS NOT NULL AND p.actual_st_time!='' AND p.actual_en_time IS NOT NULL and p.actual_en_time != '' THEN '已结束' ELSE '--' END status FROM zs_project p left join turnin_user u on p.principal=u.uid left join turnin_user u1 on p.operator=u1.uid WHERE 1 = 1
AND p.project_name LIKE "%"#{projectname}"%"
AND p.project_id LIKE "%"#{todaytime}"%"
AND p.principal = #{principal}
AND p.project_id = #{projectid}
ORDER BY p.addtime DESC
ORDER BY SUBSTRING( p.project_id,- 3 ) DESC limit 1
LIMIT #{startindex},#{pagesize}
) s where 1=1
AND s.status = #{status}

SELECT p.id as id, p.project_id as projectid, p.project_name as projectname, p.expected_st_time as expectedsttime, p.expected_en_time as expectedentime, p.actual_st_time as actualsttime, p.actual_en_time as actualentime, p.introduction as introduction, p.principal as principal, p.addtime as addtime,
MAX(SUBSTRING(p.project_id,-3)) as projectnumber,
p.updtime as updtime FROM zs_project p WHERE p.project_name = #{projectnamejq}
AND p.project_id = #{projectid}

mapper.update:
UPDATE zs_project SET
    project_name=#{projectname}

    ,expected_st_time=#{expectedsttime}


    ,expected_en_time=#{expectedentime}


    ,actual_st_time=#{actualsttime}


    ,actual_en_time=#{actualentime}


    ,introduction=#{introduction}


    ,principal=#{principal}


    ,updTime=#{updateTime}


    ,operator=#{operator}

WHERE id= #{id};

mapper.insert:
        INSERT INTO
            zs_annex (
            id
            ,annex_name
            ,annex_path
            ,annex_mark
            ,addtime
            ,updtime
            ,operator
            ,glid
            )
		VALUES
		    ( #{fileid}
		    , #{filename}
		    ,#{filepath}
		    ,#{filemark}
		    ,#{addTime}
		    ,#{updTime}
		    ,#{operator}
		    ,#{glid}
		    )
		    ON DUPLICATE KEY UPDATE id= #{fileid}

mapper.select:
SELECT a.id as fileid, a.annex_name as filename, a.annex_path as filepath, a.annex_mark as filemark FROM zs_annex a WHERE 1 = 1
AND a.glid = #{glid}
order by a.addtime desc
