mapper.insert:
        INSERT INTO
            turnin_role (
            id
            , code
            , uname
            , description
            , addTime
            , operator
             )
		VALUES
		    (
		    #{uuid}
		    , #{rolecode}
		    ,#{rolename}
		    ,#{remark}
		    ,#{addTime}
		    ,#{operator}
		    );

mapper.update:
UPDATE turnin_role SET
    uname = #{rolename}
    ,description=#{remark}
    ,updateTime=#{updateTime}
    ,operator=#{operator}
WHERE
    id= #{roleid}
    ;
