mapper.select:
SELECT t.id as id, t.demand_id as demandid, t.task_name as taskname, t.expected_st_time as expectedsttime, t.expected_en_time as expectedentime, t.actual_st_time as actualsttime, t.actual_en_time as actualentime, t.task_content as taskcontent, t.principal as principal, t.work_hours as workhours, t.schedule as schedule, t.addtime as addtime, u.uname as username, t.updtime as updtime FROM zs_task t left join turnin_user u on t.principal=u.uid WHERE 1 = 1
AND t.task_name LIKE "%"#{taskname}"%"
AND t.principal LIKE "%"#{principal}"%"
AND t.demand_id = #{demandid}
ORDER BY t.addtime DESC
LIMIT #{startindex},#{pagesize};

mapper.update:
UPDATE zs_task SET updTime=#{updateTime}

    ,task_name=#{taskname}


    ,expected_st_time=#{expectedsttime}


    ,expected_en_time=#{expectedentime}


    ,actual_st_time=#{actualsttime}


    ,actual_en_time=#{actualentime}


    ,task_content=#{taskcontent}


    ,principal=#{principal}


    ,schedule=#{schedule}


    ,work_hours=#{workhours}


    ,operator=#{operator}

WHERE id= #{id};
