mapper.select:
SELECT a.id ,b.pure_phone_number as phonenumber ,a.tradeno ,a.timeend ,a.transactionid ,c.bankname ,a.totalfee/100 as totalfee FROM g_jy_ls a LEFT JOIN g_wx_user b ON b.openid = a.openid LEFT JOIN g_wx_bank c ON c.banktype = a.banktype WHERE 1 = 1
AND a.tradeno = #{tradeno}
AND b.pure_phone_number = #{phone}
AND a.timeend >= #{startdate}
AND a.timeend <= #{enddate}
ORDER BY a.timeend DESC
LIMIT #{startindex}, #{pagesize}

SELECT COUNT(*) as cnt FROM g_jy_ls a LEFT JOIN g_wx_user b ON b.openid = a.openid LEFT JOIN g_wx_bank c ON c.banktype = a.banktype WHERE 1 = 1
AND a.tradeno = #{tradeno}
AND b.pure_phone_number = #{phone}
AND a.timeend >= #{startdate}
AND a.timeend <= #{enddate}
